export const yogaSutraVerses: Verse[] = [
  // ═══════════════════════════════════════════════════════════════════
  // PADA 1: Samadhi Pada - Chapter on Concentration (51 sutras)
  // ═══════════════════════════════════════════════════════════════════
  
  { id: 'ys-1-1', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 1, text: 'Now, the teachings of yoga.', translator: 'Swami Vivekananda', tags: ['meditation', 'discipline'], difficulty: 1, commentary: 'The opening sutra: "Atha" means "now"—indicating the student is ready to receive the teaching.' },
  { id: 'ys-1-2', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 2, text: 'Yoga is the cessation of the movements of the mind.', translator: 'Swami Vivekananda', tags: ['stillness', 'meditation'], difficulty: 2, commentary: 'The definition of yoga: chitta vritti nirodha—the stilling of the fluctuations of consciousness.' },
  { id: 'ys-1-3', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 3, text: 'Then the Seer abides in its own nature.', translator: 'Swami Vivekananda', tags: ['nonduality', 'witness', 'presence'], difficulty: 3, commentary: 'When the mind is still, pure awareness rests in itself.' },
  { id: 'ys-1-4', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 4, text: 'Otherwise, the Seer identifies with the movements of the mind.', translator: 'Swami Vivekananda', tags: ['witness', 'inquiry'], difficulty: 2 },
  { id: 'ys-1-5', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 5, text: 'The movements of the mind are fivefold. They may be painful or not painful.', translator: 'Swami Vivekananda', tags: ['inquiry', 'suffering'], difficulty: 2 },
  { id: 'ys-1-6', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 6, text: 'They are: right knowledge, wrong knowledge, imagination, sleep, and memory.', translator: 'Swami Vivekananda', tags: ['inquiry'], difficulty: 2 },
  { id: 'ys-1-7', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 7, text: 'Right knowledge is direct perception, inference, or testimony.', translator: 'Swami Vivekananda', tags: ['inquiry', 'wisdom'], difficulty: 2 },
  { id: 'ys-1-8', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 8, text: 'Wrong knowledge is a false conception not corresponding to reality.', translator: 'Swami Vivekananda', tags: ['inquiry', 'shadow'], difficulty: 2 },
  { id: 'ys-1-9', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 9, text: 'Imagination is knowledge based on words alone, without any real object.', translator: 'Swami Vivekananda', tags: ['inquiry'], difficulty: 2 },
  { id: 'ys-1-10', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 10, text: 'Sleep is the mental modification based on the absence of any content.', translator: 'Swami Vivekananda', tags: ['inquiry', 'stillness'], difficulty: 2 },
  { id: 'ys-1-11', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 11, text: 'Memory is the retention of experienced objects.', translator: 'Swami Vivekananda', tags: ['inquiry'], difficulty: 1 },
  { id: 'ys-1-12', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 12, text: 'The cessation of mental fluctuations is brought about by practice and non-attachment.', translator: 'Swami Vivekananda', tags: ['discipline', 'surrender', 'meditation'], difficulty: 2, commentary: 'Abhyasa (practice) and vairagya (non-attachment)—the twin pillars of yoga.' },
  { id: 'ys-1-13', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 13, text: 'Practice is the effort to secure steadiness.', translator: 'Swami Vivekananda', tags: ['discipline', 'stillness'], difficulty: 1 },
  { id: 'ys-1-14', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 14, text: 'This practice becomes firmly grounded when it is cultivated properly and continuously over a long period of time, with earnest devotion.', translator: 'Swami Vivekananda', tags: ['discipline', 'devotion'], difficulty: 2 },
  { id: 'ys-1-15', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 15, text: 'Non-attachment is the consciousness of mastery of one who is free from craving for objects seen or heard.', translator: 'Swami Vivekananda', tags: ['surrender', 'discipline', 'senses'], difficulty: 3 },
  { id: 'ys-1-16', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 16, text: 'The highest non-attachment is when one transcends even the gunas, through knowledge of the Self.', translator: 'Swami Vivekananda', tags: ['nonduality', 'surrender'], difficulty: 4 },
  { id: 'ys-1-17', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 17, text: 'Samprajnata samadhi is accompanied by reasoning, reflection, bliss, and a sense of pure being.', translator: 'Swami Vivekananda', tags: ['meditation', 'joy', 'presence'], difficulty: 3 },
  { id: 'ys-1-18', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 18, text: 'The other samadhi is preceded by the practice of cessation of all mental content, in which only latent impressions remain.', translator: 'Swami Vivekananda', tags: ['meditation', 'stillness'], difficulty: 4 },
  { id: 'ys-1-19', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 19, text: 'For those who are born with samadhi or have merged in nature, it comes naturally.', translator: 'Swami Vivekananda', tags: ['meditation', 'nature'], difficulty: 3 },
  { id: 'ys-1-20', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 20, text: 'For others, it is preceded by faith, energy, memory, absorption, and discernment.', translator: 'Swami Vivekananda', tags: ['devotion', 'discipline', 'meditation'], difficulty: 2 },
  { id: 'ys-1-21', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 21, text: 'Success is near for those who are intensely earnest.', translator: 'Swami Vivekananda', tags: ['discipline', 'courage'], difficulty: 1 },
  { id: 'ys-1-22', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 22, text: 'There is further distinction according to whether the practice is mild, medium, or intense.', translator: 'Swami Vivekananda', tags: ['discipline'], difficulty: 1 },
  { id: 'ys-1-23', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 23, text: 'Or, by surrender to the Lord.', translator: 'Swami Vivekananda', tags: ['devotion', 'surrender'], difficulty: 2, commentary: 'Ishvara pranidhana—surrender to the Divine is an alternate path to samadhi.' },
  { id: 'ys-1-24', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 24, text: 'The Lord is a special Self, untouched by afflictions, actions, results, or stored impressions.', translator: 'Swami Vivekananda', tags: ['devotion', 'nonduality'], difficulty: 3 },
  { id: 'ys-1-25', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 25, text: 'In the Lord, the seed of omniscience is unsurpassed.', translator: 'Swami Vivekananda', tags: ['devotion', 'wisdom'], difficulty: 2 },
  { id: 'ys-1-26', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 26, text: 'The Lord is the teacher of even the ancient teachers, being unlimited by time.', translator: 'Swami Vivekananda', tags: ['devotion', 'wisdom'], difficulty: 2 },
  { id: 'ys-1-27', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 27, text: 'The word expressing the Lord is OM.', translator: 'Swami Vivekananda', tags: ['meditation', 'devotion'], difficulty: 1 },
  { id: 'ys-1-28', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 28, text: 'Repeat it and contemplate its meaning.', translator: 'Swami Vivekananda', tags: ['meditation', 'devotion'], difficulty: 1 },
  { id: 'ys-1-29', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 29, text: 'From this comes the realization of the inner Self and the removal of obstacles.', translator: 'Swami Vivekananda', tags: ['meditation', 'nonduality'], difficulty: 2 },
  { id: 'ys-1-30', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 30, text: 'Disease, dullness, doubt, carelessness, laziness, worldliness, wrong views, non-achievement, and instability are the obstacles that distract the mind.', translator: 'Swami Vivekananda', tags: ['shadow', 'discipline'], difficulty: 2 },
  { id: 'ys-1-31', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 31, text: 'Sorrow, despair, bodily restlessness, and disturbed breathing accompany the distractions.', translator: 'Swami Vivekananda', tags: ['suffering', 'body-awareness', 'breath'], difficulty: 2 },
  { id: 'ys-1-32', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 32, text: 'To prevent these, there should be constant practice of one principle.', translator: 'Swami Vivekananda', tags: ['discipline', 'meditation'], difficulty: 2 },
  { id: 'ys-1-33', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 33, text: 'The mind becomes tranquil by cultivating friendliness toward the happy, compassion toward the suffering, joy toward the virtuous, and equanimity toward the non-virtuous.', translator: 'Swami Vivekananda', tags: ['compassion', 'joy', 'stillness'], difficulty: 2 },
  { id: 'ys-1-34', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 34, text: 'Or by the exhalation and retention of breath.', translator: 'Swami Vivekananda', tags: ['breath', 'meditation'], difficulty: 2 },
  { id: 'ys-1-35', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 35, text: 'Or by focusing on a sense perception that arises, steadiness of mind is produced.', translator: 'Swami Vivekananda', tags: ['senses', 'meditation', 'stillness'], difficulty: 2 },
  { id: 'ys-1-36', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 36, text: 'Or by contemplating the inner light, which is luminous and free from sorrow.', translator: 'Swami Vivekananda', tags: ['meditation', 'vision', 'presence'], difficulty: 3 },
  { id: 'ys-1-37', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 37, text: 'Or by contemplating a mind that is free from desire.', translator: 'Swami Vivekananda', tags: ['surrender', 'meditation'], difficulty: 2 },
  { id: 'ys-1-38', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 38, text: 'Or by meditating on knowledge gained from dreams or deep sleep.', translator: 'Swami Vivekananda', tags: ['meditation', 'mystery'], difficulty: 2 },
  { id: 'ys-1-39', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 39, text: 'Or by meditating on any object that appeals to the mind.', translator: 'Swami Vivekananda', tags: ['meditation'], difficulty: 1 },
  { id: 'ys-1-40', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 40, text: 'Mastery of the yogi extends from the smallest to the greatest.', translator: 'Swami Vivekananda', tags: ['meditation', 'presence'], difficulty: 2 },
  { id: 'ys-1-41', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 41, text: 'When the movements of the mind have been weakened, the mind becomes like a clear crystal, reflecting equally the knower, the knowing, and the known. This is samapatti.', translator: 'Swami Vivekananda', tags: ['stillness', 'nonduality', 'witness'], difficulty: 4 },
  { id: 'ys-1-42', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 42, text: 'Savitarka samapatti is when the mind alternates between word, meaning, and knowledge.', translator: 'Swami Vivekananda', tags: ['meditation', 'inquiry'], difficulty: 3 },
  { id: 'ys-1-43', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 43, text: 'Nirvitarka samapatti is when memory is purified, as it were empty of its own nature, and the object alone shines forth.', translator: 'Swami Vivekananda', tags: ['meditation', 'stillness', 'nonduality'], difficulty: 4 },
  { id: 'ys-1-44', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 44, text: 'In the same way, savichara and nirvichara samapatti, relating to subtle objects, are explained.', translator: 'Swami Vivekananda', tags: ['meditation'], difficulty: 3 },
  { id: 'ys-1-45', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 45, text: 'The province of subtle objects extends up to the formless.', translator: 'Swami Vivekananda', tags: ['meditation', 'mystery'], difficulty: 3 },
  { id: 'ys-1-46', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 46, text: 'These samapattis are only samadhi with seed.', translator: 'Swami Vivekananda', tags: ['meditation'], difficulty: 3 },
  { id: 'ys-1-47', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 47, text: 'In the clarity of nirvichara samadhi, the inner light of the Self dawns.', translator: 'Swami Vivekananda', tags: ['meditation', 'presence', 'vision'], difficulty: 4 },
  { id: 'ys-1-48', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 48, text: 'There, consciousness is filled with truth.', translator: 'Swami Vivekananda', tags: ['presence', 'nonduality'], difficulty: 3 },
  { id: 'ys-1-49', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 49, text: 'This knowledge is different from knowledge gained by testimony or inference, as it refers to particulars.', translator: 'Swami Vivekananda', tags: ['wisdom', 'presence'], difficulty: 3 },
  { id: 'ys-1-50', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 50, text: 'The impression produced by this samadhi wipes out all other impressions.', translator: 'Swami Vivekananda', tags: ['meditation', 'liberation'], difficulty: 3 },
  { id: 'ys-1-51', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 1, verseNumber: 51, text: 'When even this impression is suppressed, all being suppressed, there is seedless samadhi.', translator: 'Swami Vivekananda', tags: ['meditation', 'nonduality', 'stillness'], difficulty: 4, commentary: 'Nirbija samadhi—the seedless absorption, the highest state.' },

  // ═══════════════════════════════════════════════════════════════════
  // PADA 2: Sadhana Pada - Chapter on Practice (55 sutras)
  // ═══════════════════════════════════════════════════════════════════
  
  { id: 'ys-2-1', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 1, text: 'Kriya yoga consists of austerity, self-study, and surrender to the Lord.', translator: 'Swami Vivekananda', tags: ['discipline', 'inquiry', 'devotion'], difficulty: 2, commentary: 'Tapas, svadhyaya, Ishvara pranidhana—the threefold path of practical yoga.' },
  { id: 'ys-2-2', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 2, text: 'Its purpose is to cultivate samadhi and weaken the afflictions.', translator: 'Swami Vivekananda', tags: ['meditation', 'suffering'], difficulty: 2 },
  { id: 'ys-2-3', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 3, text: 'The afflictions are: ignorance, egoism, attachment, aversion, and clinging to life.', translator: 'Swami Vivekananda', tags: ['suffering', 'shadow', 'fear'], difficulty: 2, commentary: 'The five kleshas—the root causes of all suffering.' },
  { id: 'ys-2-4', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 4, text: 'Ignorance is the field for the others, whether dormant, attenuated, interrupted, or fully active.', translator: 'Swami Vivekananda', tags: ['shadow', 'inquiry'], difficulty: 3 },
  { id: 'ys-2-5', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 5, text: 'Ignorance is seeing the transient as eternal, the impure as pure, pain as pleasure, and the non-Self as the Self.', translator: 'Swami Vivekananda', tags: ['inquiry', 'nonduality', 'vision'], difficulty: 3 },
  { id: 'ys-2-6', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 6, text: 'Egoism is the identification of the Seer with the instrument of seeing.', translator: 'Swami Vivekananda', tags: ['inquiry', 'nonduality', 'witness'], difficulty: 3 },
  { id: 'ys-2-7', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 7, text: 'Attachment follows from pleasure.', translator: 'Swami Vivekananda', tags: ['shadow', 'senses'], difficulty: 2 },
  { id: 'ys-2-8', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 8, text: 'Aversion follows from pain.', translator: 'Swami Vivekananda', tags: ['shadow', 'suffering'], difficulty: 2 },
  { id: 'ys-2-9', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 9, text: 'Clinging to life flows on by its own momentum, even in the wise.', translator: 'Swami Vivekananda', tags: ['fear', 'death', 'shadow'], difficulty: 3 },
  { id: 'ys-2-10', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 10, text: 'These subtle afflictions can be destroyed by resolving them back into their cause.', translator: 'Swami Vivekananda', tags: ['meditation', 'liberation'], difficulty: 3 },
  { id: 'ys-2-11', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 11, text: 'Their active manifestations can be destroyed by meditation.', translator: 'Swami Vivekananda', tags: ['meditation', 'liberation'], difficulty: 2 },
  { id: 'ys-2-12', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 12, text: 'The storehouse of karma, rooted in the afflictions, is experienced in present and future lives.', translator: 'Swami Vivekananda', tags: ['action', 'death'], difficulty: 2 },
  { id: 'ys-2-13', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 13, text: 'As long as the root exists, karma ripens into birth, life span, and experience.', translator: 'Swami Vivekananda', tags: ['action', 'death'], difficulty: 2 },
  { id: 'ys-2-14', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 14, text: 'These produce joy or sorrow depending on whether the cause is virtue or vice.', translator: 'Swami Vivekananda', tags: ['action', 'joy', 'suffering'], difficulty: 2 },
  { id: 'ys-2-15', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 15, text: 'To the discriminating, all is suffering, due to the pain of change, anxiety, and latent impressions, and the conflict of the gunas.', translator: 'Swami Vivekananda', tags: ['suffering', 'nature', 'witness'], difficulty: 3 },
  { id: 'ys-2-16', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 16, text: 'Pain that has not yet come can be avoided.', translator: 'Swami Vivekananda', tags: ['suffering', 'presence'], difficulty: 1 },
  { id: 'ys-2-17', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 17, text: 'The cause of the avoidable suffering is the union of the Seer with the seen.', translator: 'Swami Vivekananda', tags: ['nonduality', 'suffering', 'witness'], difficulty: 3 },
  { id: 'ys-2-18', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 18, text: 'The seen consists of the elements and senses, has the nature of illumination, activity, and inertia, and serves the purpose of experience and liberation.', translator: 'Swami Vivekananda', tags: ['nature', 'senses', 'liberation'], difficulty: 3 },
  { id: 'ys-2-19', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 19, text: 'The stages of the gunas are the particular, the universal, the differentiated, and the undifferentiated.', translator: 'Swami Vivekananda', tags: ['nature'], difficulty: 3 },
  { id: 'ys-2-20', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 20, text: 'The Seer is pure consciousness only, though seeing through the coloring of the mind.', translator: 'Swami Vivekananda', tags: ['witness', 'nonduality', 'presence'], difficulty: 3 },
  { id: 'ys-2-21', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 21, text: 'The seen exists only for the sake of the Seer.', translator: 'Swami Vivekananda', tags: ['nonduality', 'witness'], difficulty: 3 },
  { id: 'ys-2-22', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 22, text: 'Though destroyed for one who has attained liberation, it continues to exist for others, being common to them.', translator: 'Swami Vivekananda', tags: ['liberation', 'nonduality'], difficulty: 3 },
  { id: 'ys-2-23', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 23, text: 'The union of owner and owned causes the recognition of the nature and powers of both.', translator: 'Swami Vivekananda', tags: ['nonduality', 'witness'], difficulty: 3 },
  { id: 'ys-2-24', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 24, text: 'The cause of this union is ignorance.', translator: 'Swami Vivekananda', tags: ['shadow', 'nonduality'], difficulty: 2 },
  { id: 'ys-2-25', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 25, text: 'The absence of ignorance brings the cessation of the union. This is liberation, the aloneness of the Seer.', translator: 'Swami Vivekananda', tags: ['liberation', 'nonduality', 'witness'], difficulty: 3 },
  { id: 'ys-2-26', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 26, text: 'Unbroken discriminative awareness is the means to liberation.', translator: 'Swami Vivekananda', tags: ['witness', 'presence', 'nonduality'], difficulty: 3 },
  { id: 'ys-2-27', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 27, text: 'The yogi\'s wisdom unfolds in seven stages.', translator: 'Swami Vivekananda', tags: ['wisdom', 'meditation'], difficulty: 2 },
  { id: 'ys-2-28', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 28, text: 'Through the practice of the limbs of yoga, impurities are destroyed. Then the light of knowledge dawns, culminating in discriminative awareness.', translator: 'Swami Vivekananda', tags: ['discipline', 'wisdom', 'liberation'], difficulty: 2 },
  { id: 'ys-2-29', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 29, text: 'The eight limbs of yoga are: restraints, observances, posture, breath control, sense withdrawal, concentration, meditation, and absorption.', translator: 'Swami Vivekananda', tags: ['discipline', 'meditation'], difficulty: 2, commentary: 'The famous ashtanga—eight limbs of yoga.' },
  { id: 'ys-2-30', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 30, text: 'The restraints are: non-violence, truthfulness, non-stealing, continence, and non-greed.', translator: 'Swami Vivekananda', tags: ['discipline', 'compassion'], difficulty: 2 },
  { id: 'ys-2-31', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 31, text: 'These, when practiced universally without exception of birth, place, time, or circumstance, become the great vow.', translator: 'Swami Vivekananda', tags: ['discipline'], difficulty: 2 },
  { id: 'ys-2-32', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 32, text: 'The observances are: purity, contentment, austerity, self-study, and surrender to the Lord.', translator: 'Swami Vivekananda', tags: ['discipline', 'devotion'], difficulty: 2 },
  { id: 'ys-2-33', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 33, text: 'When disturbed by disturbing thoughts, cultivate the opposite.', translator: 'Swami Vivekananda', tags: ['discipline', 'meditation'], difficulty: 2 },
  { id: 'ys-2-34', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 34, text: 'Disturbing thoughts—violence and the rest—whether done, caused, or approved, whether arising from greed, anger, or delusion, whether mild, medium, or intense, result in endless suffering and ignorance. Therefore, cultivate the opposite.', translator: 'Swami Vivekananda', tags: ['shadow', 'anger', 'suffering'], difficulty: 2 },
  { id: 'ys-2-35', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 35, text: 'When non-violence is firmly established, hostility ceases in its presence.', translator: 'Swami Vivekananda', tags: ['compassion', 'stillness'], difficulty: 2 },
  { id: 'ys-2-36', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 36, text: 'When truthfulness is firmly established, action and its fruit become dependent on it.', translator: 'Swami Vivekananda', tags: ['action', 'discipline'], difficulty: 2 },
  { id: 'ys-2-37', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 37, text: 'When non-stealing is firmly established, all jewels present themselves.', translator: 'Swami Vivekananda', tags: ['discipline', 'joy'], difficulty: 2 },
  { id: 'ys-2-38', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 38, text: 'When continence is firmly established, great vitality is gained.', translator: 'Swami Vivekananda', tags: ['discipline', 'strength'], difficulty: 2 },
  { id: 'ys-2-39', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 39, text: 'When non-greed is firmly established, the knowledge of the why and how of existence arises.', translator: 'Swami Vivekananda', tags: ['surrender', 'wisdom'], difficulty: 2 },
  { id: 'ys-2-40', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 40, text: 'From purity comes disgust with one\'s own body and non-contact with others.', translator: 'Swami Vivekananda', tags: ['body-awareness', 'discipline'], difficulty: 2 },
  { id: 'ys-2-41', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 41, text: 'From mental purity come cheerfulness, one-pointedness, sense control, and fitness for Self-realization.', translator: 'Swami Vivekananda', tags: ['joy', 'senses', 'nonduality'], difficulty: 2 },
  { id: 'ys-2-42', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 42, text: 'From contentment, supreme happiness is gained.', translator: 'Swami Vivekananda', tags: ['joy', 'stillness'], difficulty: 1 },
  { id: 'ys-2-43', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 43, text: 'From austerity, due to the destruction of impurities, comes perfection of the body and senses.', translator: 'Swami Vivekananda', tags: ['discipline', 'body-awareness', 'senses'], difficulty: 2 },
  { id: 'ys-2-44', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 44, text: 'From self-study comes union with the chosen deity.', translator: 'Swami Vivekananda', tags: ['inquiry', 'devotion'], difficulty: 2 },
  { id: 'ys-2-45', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 45, text: 'From surrender to the Lord comes perfection of samadhi.', translator: 'Swami Vivekananda', tags: ['devotion', 'surrender', 'meditation'], difficulty: 2 },
  { id: 'ys-2-46', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 46, text: 'Posture should be steady and comfortable.', translator: 'Swami Vivekananda', tags: ['body-awareness', 'stillness'], difficulty: 1 },
  { id: 'ys-2-47', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 47, text: 'Posture is mastered by relaxing effort and meditating on the infinite.', translator: 'Swami Vivekananda', tags: ['surrender', 'stillness', 'nonduality'], difficulty: 2 },
  { id: 'ys-2-48', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 48, text: 'From that, one is not afflicted by the pairs of opposites.', translator: 'Swami Vivekananda', tags: ['nonduality', 'stillness'], difficulty: 2 },
  { id: 'ys-2-49', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 49, text: 'When this is achieved, pranayama, the regulation of breath, follows.', translator: 'Swami Vivekananda', tags: ['breath', 'discipline'], difficulty: 2 },
  { id: 'ys-2-50', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 50, text: 'Pranayama consists of exhalation, inhalation, and retention. It is regulated by place, time, and number, becoming prolonged and subtle.', translator: 'Swami Vivekananda', tags: ['breath', 'discipline'], difficulty: 2 },
  { id: 'ys-2-51', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 51, text: 'The fourth pranayama transcends the external and internal.', translator: 'Swami Vivekananda', tags: ['breath', 'nonduality'], difficulty: 3 },
  { id: 'ys-2-52', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 52, text: 'From that, the covering of the inner light is removed.', translator: 'Swami Vivekananda', tags: ['breath', 'vision'], difficulty: 3 },
  { id: 'ys-2-53', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 53, text: 'And the mind becomes fit for concentration.', translator: 'Swami Vivekananda', tags: ['breath', 'meditation'], difficulty: 2 },
  { id: 'ys-2-54', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 54, text: 'Pratyahara, sense withdrawal, is when the senses withdraw from their objects and assume the nature of the mind.', translator: 'Swami Vivekananda', tags: ['senses', 'stillness', 'meditation'], difficulty: 3 },
  { id: 'ys-2-55', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 2, verseNumber: 55, text: 'From that comes supreme mastery over the senses.', translator: 'Swami Vivekananda', tags: ['senses', 'discipline'], difficulty: 2 },

  // ═══════════════════════════════════════════════════════════════════
  // PADA 3: Vibhuti Pada - Chapter on Powers (56 sutras)
  // ═══════════════════════════════════════════════════════════════════
  
  { id: 'ys-3-1', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 1, text: 'Dharana, concentration, is holding the mind in one place.', translator: 'Swami Vivekananda', tags: ['meditation', 'discipline', 'stillness'], difficulty: 2 },
  { id: 'ys-3-2', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 2, text: 'Dhyana, meditation, is the continuous flow of attention toward that point.', translator: 'Swami Vivekananda', tags: ['meditation', 'stillness'], difficulty: 2 },
  { id: 'ys-3-3', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 3, text: 'Samadhi is when that meditation shines forth as the object alone, as if empty of its own form.', translator: 'Swami Vivekananda', tags: ['nonduality', 'stillness', 'presence'], difficulty: 4, commentary: 'Samadhi: the mind becomes so absorbed that only the object of meditation shines forth.' },
  { id: 'ys-3-4', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 4, text: 'These three together constitute samyama.', translator: 'Swami Vivekananda', tags: ['meditation'], difficulty: 2 },
  { id: 'ys-3-5', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 5, text: 'By mastery of samyama comes the light of insight.', translator: 'Swami Vivekananda', tags: ['vision', 'meditation'], difficulty: 3 },
  { id: 'ys-3-6', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 6, text: 'Samyama should be applied in stages.', translator: 'Swami Vivekananda', tags: ['meditation', 'discipline'], difficulty: 2 },
  { id: 'ys-3-7', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 7, text: 'These three are more internal than the preceding.', translator: 'Swami Vivekananda', tags: ['meditation'], difficulty: 2 },
  { id: 'ys-3-8', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 8, text: 'But even these are external to the seedless samadhi.', translator: 'Swami Vivekananda', tags: ['meditation', 'nonduality'], difficulty: 3 },
  { id: 'ys-3-9', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 9, text: 'The suppression of the latent impression of emergence and the appearance of the latent impression of suppression, occurring at the moment of suppression, is the transformation of suppression.', translator: 'Swami Vivekananda', tags: ['meditation', 'stillness'], difficulty: 4 },
  { id: 'ys-3-10', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 10, text: 'Its peaceful flow comes from latent impression.', translator: 'Swami Vivekananda', tags: ['stillness', 'meditation'], difficulty: 3 },
  { id: 'ys-3-11', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 11, text: 'The transformation of samadhi is the dwindling of distraction and the arising of one-pointedness.', translator: 'Swami Vivekananda', tags: ['meditation', 'stillness'], difficulty: 3 },
  { id: 'ys-3-12', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 12, text: 'The transformation of one-pointedness is when the past and present objects are the same.', translator: 'Swami Vivekananda', tags: ['meditation', 'stillness', 'presence'], difficulty: 3 },
  { id: 'ys-3-13', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 13, text: 'By these, the transformations of characteristic, time-state, and condition in elements and senses are explained.', translator: 'Swami Vivekananda', tags: ['nature', 'senses'], difficulty: 3 },
  { id: 'ys-3-14', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 14, text: 'The substratum is that which conforms to past, present, and future characteristics.', translator: 'Swami Vivekananda', tags: ['presence', 'nonduality'], difficulty: 3 },
  { id: 'ys-3-15', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 15, text: 'The difference in sequence is the cause of the difference in transformation.', translator: 'Swami Vivekananda', tags: ['nature'], difficulty: 3 },
  { id: 'ys-3-16', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 16, text: 'By samyama on the three transformations comes knowledge of past and future.', translator: 'Swami Vivekananda', tags: ['meditation', 'wisdom'], difficulty: 3 },
  { id: 'ys-3-17', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 17, text: 'Word, object, and idea are confused due to mutual superimposition. By samyama on their distinction comes knowledge of the sounds of all beings.', translator: 'Swami Vivekananda', tags: ['meditation', 'wisdom'], difficulty: 3 },
  { id: 'ys-3-18', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 18, text: 'By direct perception of latent impressions comes knowledge of previous births.', translator: 'Swami Vivekananda', tags: ['meditation', 'death'], difficulty: 3 },
  { id: 'ys-3-19', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 19, text: 'By samyama on thoughts comes knowledge of other minds.', translator: 'Swami Vivekananda', tags: ['meditation', 'compassion'], difficulty: 3 },
  { id: 'ys-3-20', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 20, text: 'But not of the object of that thought, as that is not the object of the samyama.', translator: 'Swami Vivekananda', tags: ['meditation'], difficulty: 2 },
  { id: 'ys-3-21', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 21, text: 'By samyama on the form of the body, when its power of being perceived is obstructed, the eye not being in contact with light, invisibility results.', translator: 'Swami Vivekananda', tags: ['meditation', 'body-awareness'], difficulty: 3 },
  { id: 'ys-3-22', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 22, text: 'By this, the disappearance of sound and other objects of the senses is also explained.', translator: 'Swami Vivekananda', tags: ['senses', 'meditation'], difficulty: 3 },
  { id: 'ys-3-23', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 23, text: 'Karma is of two kinds: that which will soon bear fruit and that which will not. By samyama on these, or from omens, comes knowledge of death.', translator: 'Swami Vivekananda', tags: ['action', 'death'], difficulty: 3 },
  { id: 'ys-3-24', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 24, text: 'By samyama on friendliness and the like, those powers are obtained.', translator: 'Swami Vivekananda', tags: ['compassion', 'meditation'], difficulty: 2 },
  { id: 'ys-3-25', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 25, text: 'By samyama on the strength of an elephant, that strength is obtained.', translator: 'Swami Vivekananda', tags: ['strength', 'meditation'], difficulty: 2 },
  { id: 'ys-3-26', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 26, text: 'By directing the light of higher sense perception, knowledge of subtle, hidden, and remote objects is obtained.', translator: 'Swami Vivekananda', tags: ['vision', 'meditation'], difficulty: 3 },
  { id: 'ys-3-27', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 27, text: 'By samyama on the sun comes knowledge of the cosmic regions.', translator: 'Swami Vivekananda', tags: ['meditation', 'wisdom'], difficulty: 2 },
  { id: 'ys-3-28', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 28, text: 'By samyama on the moon comes knowledge of the arrangement of the stars.', translator: 'Swami Vivekananda', tags: ['meditation', 'wisdom'], difficulty: 2 },
  { id: 'ys-3-29', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 29, text: 'By samyama on the pole star comes knowledge of the movement of the stars.', translator: 'Swami Vivekananda', tags: ['meditation', 'wisdom'], difficulty: 2 },
  { id: 'ys-3-30', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 30, text: 'By samyama on the navel center comes knowledge of the arrangement of the body.', translator: 'Swami Vivekananda', tags: ['body-awareness', 'meditation'], difficulty: 2 },
  { id: 'ys-3-31', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 31, text: 'By samyama on the throat pit comes cessation of hunger and thirst.', translator: 'Swami Vivekananda', tags: ['body-awareness', 'meditation'], difficulty: 2 },
  { id: 'ys-3-32', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 32, text: 'By samyama on the tortoise channel comes steadiness.', translator: 'Swami Vivekananda', tags: ['stillness', 'meditation'], difficulty: 2 },
  { id: 'ys-3-33', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 33, text: 'By samyama on the light at the crown of the head comes vision of the perfected ones.', translator: 'Swami Vivekananda', tags: ['vision', 'meditation'], difficulty: 3 },
  { id: 'ys-3-34', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 34, text: 'Or from intuition, all knowledge arises.', translator: 'Swami Vivekananda', tags: ['wisdom', 'presence'], difficulty: 2 },
  { id: 'ys-3-35', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 35, text: 'By samyama on the heart comes knowledge of the mind.', translator: 'Swami Vivekananda', tags: ['meditation', 'wisdom'], difficulty: 2 },
  { id: 'ys-3-36', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 36, text: 'Experience is the inability to distinguish between the pure Self and the intelligence, which are utterly different. By samyama on the Self-interest rather than the interest of another comes knowledge of the Self.', translator: 'Swami Vivekananda', tags: ['nonduality', 'witness'], difficulty: 4 },
  { id: 'ys-3-37', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 37, text: 'From this arises intuitive hearing, touching, seeing, tasting, and smelling.', translator: 'Swami Vivekananda', tags: ['senses', 'presence'], difficulty: 3 },
  { id: 'ys-3-38', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 38, text: 'These are obstacles to samadhi, but powers in the world.', translator: 'Swami Vivekananda', tags: ['meditation', 'shadow'], difficulty: 2 },
  { id: 'ys-3-39', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 39, text: 'By loosening the cause of bondage and by knowledge of the passages, the mind can enter another body.', translator: 'Swami Vivekananda', tags: ['meditation', 'mystery'], difficulty: 3 },
  { id: 'ys-3-40', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 40, text: 'By mastery of udana comes levitation over water, mud, thorns, and the like, and death at will.', translator: 'Swami Vivekananda', tags: ['breath', 'death'], difficulty: 3 },
  { id: 'ys-3-41', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 41, text: 'By mastery of samana comes radiance.', translator: 'Swami Vivekananda', tags: ['breath', 'vision'], difficulty: 2 },
  { id: 'ys-3-42', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 42, text: 'By samyama on the relation between ear and space comes divine hearing.', translator: 'Swami Vivekananda', tags: ['senses', 'meditation'], difficulty: 2 },
  { id: 'ys-3-43', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 43, text: 'By samyama on the relation between body and space, or by samapatti on lightness like cotton, comes passage through space.', translator: 'Swami Vivekananda', tags: ['body-awareness', 'meditation'], difficulty: 3 },
  { id: 'ys-3-44', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 44, text: 'The great out-of-body experience is actual and unconceived. From that, the covering of light is removed.', translator: 'Swami Vivekananda', tags: ['meditation', 'mystery'], difficulty: 3 },
  { id: 'ys-3-45', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 45, text: 'By samyama on the gross, essential nature, subtlety, interconnection, and purpose of the elements comes mastery over them.', translator: 'Swami Vivekananda', tags: ['nature', 'meditation'], difficulty: 3 },
  { id: 'ys-3-46', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 46, text: 'From that comes the appearance of the powers like atomic size, perfection of the body, and indestructibility of its qualities.', translator: 'Swami Vivekananda', tags: ['body-awareness', 'meditation'], difficulty: 3 },
  { id: 'ys-3-47', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 47, text: 'Perfection of the body consists of beauty, grace, strength, and adamantine hardness.', translator: 'Swami Vivekananda', tags: ['body-awareness', 'strength'], difficulty: 2 },
  { id: 'ys-3-48', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 48, text: 'By samyama on the grasping, essential nature, egoism, interconnection, and purpose of the senses comes mastery over them.', translator: 'Swami Vivekananda', tags: ['senses', 'meditation'], difficulty: 3 },
  { id: 'ys-3-49', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 49, text: 'From that comes speed like the mind, perception without the senses, and mastery over primordial nature.', translator: 'Swami Vivekananda', tags: ['nature', 'senses'], difficulty: 3 },
  { id: 'ys-3-50', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 50, text: 'Only from the knowledge of the distinction between intelligence and the Self comes omnipotence and omniscience.', translator: 'Swami Vivekananda', tags: ['nonduality', 'witness', 'wisdom'], difficulty: 4 },
  { id: 'ys-3-51', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 51, text: 'By non-attachment even to these comes destruction of the seed of bondage, and liberation.', translator: 'Swami Vivekananda', tags: ['surrender', 'liberation'], difficulty: 3 },
  { id: 'ys-3-52', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 52, text: 'When invited by celestial beings, there should be neither attachment nor pride, because of the possibility of renewed undesirable contact.', translator: 'Swami Vivekananda', tags: ['surrender', 'discipline'], difficulty: 3 },
  { id: 'ys-3-53', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 53, text: 'By samyama on moment and sequence comes discriminative knowledge.', translator: 'Swami Vivekananda', tags: ['presence', 'wisdom'], difficulty: 3 },
  { id: 'ys-3-54', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 54, text: 'From this comes the ability to distinguish between two similar things when they cannot be differentiated by class, characteristic, or position.', translator: 'Swami Vivekananda', tags: ['wisdom', 'vision'], difficulty: 3 },
  { id: 'ys-3-55', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 55, text: 'The liberating discriminative knowledge is the knowledge of all objects simultaneously in all conditions.', translator: 'Swami Vivekananda', tags: ['wisdom', 'nonduality', 'liberation'], difficulty: 4 },
  { id: 'ys-3-56', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 3, verseNumber: 56, text: 'When the purity of the intelligence is equal to the purity of the Self, there is liberation.', translator: 'Swami Vivekananda', tags: ['nonduality', 'liberation'], difficulty: 4, commentary: 'The culmination of practice: the mind becomes as pure as pure awareness itself.' },

  // ═══════════════════════════════════════════════════════════════════
  // PADA 4: Kaivalya Pada - Chapter on Liberation (34 sutras)
  // ═══════════════════════════════════════════════════════════════════
  
  { id: 'ys-4-1', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 1, text: 'Powers arise from birth, herbs, mantras, austerity, or samadhi.', translator: 'Swami Vivekananda', tags: ['discipline', 'meditation'], difficulty: 2 },
  { id: 'ys-4-2', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 2, text: 'The transformation into another state of existence is by the flow of creative nature.', translator: 'Swami Vivekananda', tags: ['nature', 'death'], difficulty: 3 },
  { id: 'ys-4-3', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 3, text: 'The incidental cause does not move the creative nature into action; it merely removes obstacles, like a farmer removing obstacles for water to flow.', translator: 'Swami Vivekananda', tags: ['nature', 'action'], difficulty: 3 },
  { id: 'ys-4-4', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 4, text: 'Created minds arise from ego alone.', translator: 'Swami Vivekananda', tags: ['shadow', 'inquiry'], difficulty: 2 },
  { id: 'ys-4-5', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 5, text: 'One mind directs the many minds in their various activities.', translator: 'Swami Vivekananda', tags: ['nonduality', 'witness'], difficulty: 3 },
  { id: 'ys-4-6', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 6, text: 'Of these, the mind born of meditation is free from karmic impressions.', translator: 'Swami Vivekananda', tags: ['meditation', 'liberation'], difficulty: 3 },
  { id: 'ys-4-7', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 7, text: 'The karma of a yogi is neither white nor black. For others, it is of three kinds.', translator: 'Swami Vivekananda', tags: ['action', 'nonduality'], difficulty: 3 },
  { id: 'ys-4-8', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 8, text: 'From that come the manifestation of only those latent impressions that correspond to the fruition of that karma.', translator: 'Swami Vivekananda', tags: ['action'], difficulty: 3 },
  { id: 'ys-4-9', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 9, text: 'There is continuity of memory and latent impressions, even though they are separated by birth, place, and time, due to their identity.', translator: 'Swami Vivekananda', tags: ['death'], difficulty: 3 },
  { id: 'ys-4-10', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 10, text: 'They are without beginning, because the desire for life is eternal.', translator: 'Swami Vivekananda', tags: ['fear', 'death'], difficulty: 3 },
  { id: 'ys-4-11', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 11, text: 'Being held together by cause, effect, basis, and support, when these are absent, the impressions are absent.', translator: 'Swami Vivekananda', tags: ['liberation'], difficulty: 3 },
  { id: 'ys-4-12', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 12, text: 'The past and future exist in their own forms. The difference is due to the difference in the paths of the characteristics.', translator: 'Swami Vivekananda', tags: ['presence', 'nature'], difficulty: 3 },
  { id: 'ys-4-13', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 13, text: 'They are manifest or subtle, according to the nature of the gunas.', translator: 'Swami Vivekananda', tags: ['nature'], difficulty: 2 },
  { id: 'ys-4-14', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 14, text: 'The reality of an object is due to the uniqueness of the transformation.', translator: 'Swami Vivekananda', tags: ['nature', 'nonduality'], difficulty: 3 },
  { id: 'ys-4-15', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 15, text: 'Due to the difference in minds, the way of perceiving is different, although the object is the same.', translator: 'Swami Vivekananda', tags: ['vision', 'nonduality'], difficulty: 3 },
  { id: 'ys-4-16', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 16, text: 'An object is not dependent on a single mind, for if it were, what would happen when that mind did not perceive it?', translator: 'Swami Vivekananda', tags: ['inquiry', 'nonduality'], difficulty: 3 },
  { id: 'ys-4-17', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 17, text: 'An object is known or unknown according to whether or not the mind is colored by it.', translator: 'Swami Vivekananda', tags: ['witness', 'vision'], difficulty: 3 },
  { id: 'ys-4-18', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 18, text: 'The modifications of the mind are always known by its master, the Self, due to the unchanging nature of that Self.', translator: 'Swami Vivekananda', tags: ['witness', 'nonduality', 'presence'], difficulty: 3 },
  { id: 'ys-4-19', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 19, text: 'The mind is not self-illuminating, because it is the object of perception.', translator: 'Swami Vivekananda', tags: ['nonduality', 'witness'], difficulty: 3 },
  { id: 'ys-4-20', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 20, text: 'And there cannot be perception of both at the same time.', translator: 'Swami Vivekananda', tags: ['witness', 'nonduality'], difficulty: 3 },
  { id: 'ys-4-21', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 21, text: 'If cognition by one mind were cognized by another, there would be infinite regress and confusion of memory.', translator: 'Swami Vivekananda', tags: ['inquiry', 'nonduality'], difficulty: 3 },
  { id: 'ys-4-22', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 22, text: 'When consciousness assumes the form of the unchanging Self, self-knowledge arises.', translator: 'Swami Vivekananda', tags: ['nonduality', 'witness', 'presence'], difficulty: 4 },
  { id: 'ys-4-23', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 23, text: 'The mind, colored by both the Seer and the seen, comprehends everything.', translator: 'Swami Vivekananda', tags: ['witness', 'vision'], difficulty: 3 },
  { id: 'ys-4-24', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 24, text: 'Though variegated by countless impressions, the mind exists for another, as it acts in combination.', translator: 'Swami Vivekananda', tags: ['nonduality', 'witness'], difficulty: 3 },
  { id: 'ys-4-25', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 25, text: 'For one who sees the distinction between the mind and the Self, inquiry into the nature of self ceases.', translator: 'Swami Vivekananda', tags: ['nonduality', 'inquiry', 'stillness'], difficulty: 4 },
  { id: 'ys-4-26', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 26, text: 'Then the mind inclines toward discrimination and gravitates toward liberation.', translator: 'Swami Vivekananda', tags: ['nonduality', 'vision'], difficulty: 3 },
  { id: 'ys-4-27', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 27, text: 'In the intervals, other thoughts arise from latent impressions.', translator: 'Swami Vivekananda', tags: ['shadow', 'meditation'], difficulty: 2 },
  { id: 'ys-4-28', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 28, text: 'Their removal is like that of the afflictions, as described earlier.', translator: 'Swami Vivekananda', tags: ['liberation', 'meditation'], difficulty: 2 },
  { id: 'ys-4-29', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 29, text: 'For one who has no self-interest even in the highest illumination and maintains discriminative awareness at all times, there comes dharma megha samadhi, the cloud of virtue.', translator: 'Swami Vivekananda', tags: ['nonduality', 'stillness', 'surrender'], difficulty: 4 },
  { id: 'ys-4-30', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 30, text: 'From that comes cessation of afflictions and karma.', translator: 'Swami Vivekananda', tags: ['surrender', 'nonduality'], difficulty: 3 },
  { id: 'ys-4-31', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 31, text: 'Then all coverings and impurities being removed, because of the infinity of that knowledge, little remains to be known.', translator: 'Swami Vivekananda', tags: ['wisdom', 'liberation'], difficulty: 3 },
  { id: 'ys-4-32', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 32, text: 'Then the gunas, having fulfilled their purpose, attain the end of their sequence of changes.', translator: 'Swami Vivekananda', tags: ['nature', 'liberation'], difficulty: 3 },
  { id: 'ys-4-33', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 33, text: 'Sequence is the succession of moments, apprehensible at the end of the transformation.', translator: 'Swami Vivekananda', tags: ['presence', 'nonduality'], difficulty: 3 },
  { id: 'ys-4-34', sourceId: 'yoga-sutras', sourceName: 'Yoga Sutras', tradition: 'Hindu', chapter: 4, verseNumber: 34, text: 'Liberation is when the gunas, having fulfilled their purpose, return to their original state, or when the power of consciousness is established in its own nature.', translator: 'Swami Vivekananda', tags: ['nonduality', 'stillness', 'presence'], difficulty: 4, commentary: 'The final sutra: kaivalya—the aloneness of pure awareness, established in its own nature.' },
];

export default yogaSutraVerses;
